INSERT INTO `product` VALUES (1,'Orange Juice',4,'On the top ten important things to have in a bar, orange juice is a must have. Fresh squeezed such is preferable.',NULL,'https://s3.ca-central-1.amazonaws.com/commonwealthcocktailsimages','orange-juice-glass-bottle-1-pint.jpg','Mortons Dairies','http://www.mortonsdairies.co.uk/media/products/orange-juice-glass-bottle-1-pint.jpg',NULL,NULL,1,'Fresh Squeezed',NULL,NULL,NULL,NULL,NULL),(2,'Lemon Juice',4,'Oh boy, this mixer is about as essential as it gets. Try to always use fresh squeezed juice! The one excuse for not using it is‚Ä¶ No, we cant think of any.',NULL,'https://s3.ca-central-1.amazonaws.com/commonwealthcocktailsimages','lemon.png','Dr Deborah Baker','http://www.drdeborahbaker.com/wp-content/uploads/2014/07/lemon.png',NULL,NULL,1,'Fresh Squeezed',NULL,NULL,NULL,NULL,NULL),(3,'Whiskey - Bourbon',1,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,NULL,NULL,NULL,NULL,NULL,NULL),(4,'Irish Cream Liqueur',2,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,NULL,NULL,NULL,NULL,NULL,NULL),(5,'Hazelnut Liqueur',2,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,NULL,NULL,NULL,NULL,NULL,NULL),(6,'Maple Syrup',4,NULL,NULL,'https://s3.ca-central-1.amazonaws.com/commonwealthcocktailsimages','Maple_Leaf_Bottle.jpg','McCamus Maple Syrup','http://cdn.shopify.com/s/files/1/0628/8453/products/Leaf_Bottle.jpg?v=1415141353',NULL,NULL,1,NULL,NULL,NULL,NULL,NULL,NULL),(7,'Coffee Liqueur',2,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,NULL,NULL,NULL,NULL,NULL,NULL),(8,'Milk',4,NULL,NULL,'https://s3.ca-central-1.amazonaws.com/commonwealthcocktailsimages','milk.png','PngImg.com','http://pngimg.com/upload/milk_PNG12746.png',NULL,NULL,1,NULL,NULL,NULL,NULL,NULL,NULL),(9,'Cream',4,NULL,NULL,'https://s3.ca-central-1.amazonaws.com/commonwealthcocktailsimages','cream.jpg','PngImg.com','http://pngimg.com/upload/bottle_PNG2934.png',NULL,NULL,1,NULL,NULL,NULL,NULL,NULL,NULL),(10,'Coconut Rum Liqueur',2,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,NULL,NULL,NULL,NULL,NU