INSERT INTO `recipe` VALUES (1,'&lt;p&gt;Combine all of the ingredients in an ice filled cocktail shaker. Cover, shake well, and pour into a Rocks glass. Add a couple of sipping straws, garnish accordingly.&lt;/p&gt;'),(2,'Muddle ginger in base of shaker. Add other ingredients, shake with ice and fine strain into ice-filled glass.'),(3,'Shake all ingredients with ice and strain into ice-filled glass.'),(4,'Shake all ingredients with ice and strain into ice-filled glass.'),(5,'Dry Shake (without ice) all ingredients to emulsify. Add ice, shake again and strain into ice-filled glass.'),(6,'&lt;p&gt;Shake and strain into a chilled cocktail glass.&lt;/p&gt;'),(8,'&lt;p&gt;Pour all ingredients into a shaker with ice. Shake. Strain into a cocktail glass.&lt;/p&gt;'),(9,'&lt;p&gt;Shake and strain into a chilled cocktail glass.&lt;/p&gt;'),(10,'Combine all of the ingredients in an ice filled cocktail shaker.  Cover, shake well, and pour into a Rocks glass.  Add a couple of sipping straws, garnish accordingly.'),(11,'Muddle ginger in base of shaker. Add other ingredients, shake with ice and fine strain into ice-filled glass.'),(12,'Shake all ingredients with ice and strain into ice-filled glass.'),(13,'Shake all ingredients with ice and strain into ice-filled glass.'),(14,'Dry Shake (without ice) all ingredients to emulsify. Add ice, shake again and strain into ice-filled glass.'),(15,'Shake and strain into a chilled cocktail glass.'),(16,'Mix the first two ingredients directly in an old-fashioned glass filled with ice-cubes, then add a splash of soda water.'),(17,'Pour all ingredients into a shaker with ice. Shake. Strain into a cocktail glass.'),(18,'Shake and strain into a chilled cocktail glass.'),(48,'&lt;p&gt;Mix the ingredients directly in an old-fashioned glass filled with ice-cubes, add a splash of soda water and garnish with half orange slice!&lt;/p&gt;'),(49,'&lt;p&gt;Mix the ingredients directly in a glass filled with ice-cubes and garnish with a lime wedge and a straw.&lt;br&gt;&lt;/p&gt;'),(50,'&lt;p&gt;Mix the ingredients directly in a glass filled with ice-cubes and garnish with a lime wedge and a straw.&lt;br&gt;&lt;/p&gt;'),(51,'&lt;p&gt;Pour all ingredients into shaker with ice cubes, shake well, strain into chilled cocktail glass.&lt;/p&gt;'),(52,'&lt;p&gt;Pour all ingredients into shaker with ice cubes, shake, strain into chilled cocktail glass.&lt;/p&gt;'),(53,'&lt;p&gt;Pour all ingredients into shaker with ice cubes, shake well. Strain into chilled cocktail glass and garnish with a lemon twist and a marachino cherry.&lt;/p&gt;'),(54,'&lt;p&gt;Pour all ingredients into cocktail shaker filled with ice. Shake well. Strain into cocktail glass.&lt;/p&gt;'),(55,'&lt;p&gt;Shake and pour into an ice filled glass.&lt;/p&gt;'),(56,'&lt;p&gt;Pour all ingredients into a mixing glass with ice. Stir. Strain into a cocktail glass.&lt;/p&gt;'),(57,'&lt;p&gt;Pour all ingredients into mixing glass with ice cubes. Stir well. Strain into chilled martini glass.&lt;/p&gt;'),(58,'&lt;p&gt;Shake all ingredients with ice cubes, except soda water. Pour into the tumbler. Top with soda water.&lt;/p&gt;'),(59,'&lt;p&gt;Pour all ingredients directly into highball glass filled with ice. Stir gently.&lt;/p&gt;');
